CREATE TABLE if NOT EXISTS USERS (id VARCHAR(255) NOT NULL, name VARCHAR(255), email VARCHAR(255), password VARCHAR(255), created_at DATE, updated_at DATE,PRIMARY KEY (id));
CREATE TABLE if NOT EXISTS PHONE (id INT NOT NULL AUTO_INCREMENT,NUMBER VARCHAR(255), CITY_CODE  VARCHAR(255), COUNTRY_CODE  VARCHAR(255), ID_USER VARCHAR(255), PRIMARY KEY (id), FOREIGN KEY (ID_USER) REFERENCES USERS(id) );